Widget.Code:

    $rootClass = $perc.widget.item.cssProperties.get('rootclass');
    $isEditMode = $perc.isEditMode() || $perc.isPreviewMode();
    $titleFormat = $perc.widget.item.properties.get('titleFormat');
    $blogPostNextPost = $perc.widget.item.properties.get('blogPostNextPost');
    $blogPostPrePost = $perc.widget.item.properties.get('blogPostPrePost');
    $blogPostDateTimeFormat = $perc.widget.item.properties.get('blogPostDateTimeFormat');
    $newerText = $blogPostNextPost;
    $olderText = $blogPostPrePost;
    $newerPostClass = 'perc-newer-post';
    $olderPostClass = 'perc-older-post';
    $blogPostBylineText = $perc.widget.item.properties.get('blogPostBylineText');
    $locale = $perc.widget.item.properties.get('locale');
    if($locale == null)
       $locale = "en-US";
    $blogNavigationContext = $perc.widget.item.properties.get('blogNavigationContext');
    if($blogNavigationContext == 'blogTitle')
    {
        $newerText = 'Newer Post Title';
        $olderText = 'Older Post Title';
        $olderPostClass = 'perc-older-post-title';
        $newerPostClass = 'perc-newer-post-title';
    }



    $blogNavigationLocation = $perc.widget.item.properties.get('blogNavigationLocation');
    $assets = $rx.pageutils.widgetContents($sys.assemblyItem, $perc.widget, null, null);
    $perc.setWidgetContents($assets);
    if (!empty($titleFormat))
    {
        $titleFormatStartTag = '<' + $titleFormat + ' class="perc-blog-title">';
        $titleFormatEndTag = '</' + $titleFormat + '>';
    }
    if (! $assets.isEmpty())
    {
        $assetItem = $assets.get(0);
        $blogAuthorName = $assetItem.Node.getProperty('authorname').getString();
        $bylineStart = '<div class="perc-blog-byline"><span class="perc-blog-author-label">' + $blogPostBylineText + ' </span><span class="perc-blog-author-name">';
        $bylineEnd = '</span></div>';
        $percHidefieldByline = $perc.widget.item.properties.get('perc_hidefield_byline');
        $percHidefieldBlogDate = $perc.widget.item.properties.get('perc_hidefield_blog_date');
        $percHidefieldTags = $perc.widget.item.properties.get('perc_hidefield_tags');
        $percTagsLabel = $perc.widget.item.properties.get('perc_tags_label');
        $percHidefieldCategories = $perc.widget.item.properties.get('perc_hidefield_categories');
        $percCategoriesLabel = $perc.widget.item.properties.get('perc_categories_label');
        $trackBlogPost = $perc.widget.item.properties.get('trackBlogPost');
        $blogPostFullPath = $rx.pageutils.itemLink($perc.linkContext, $perc.getPage()).toString();


        ## Get navigation.  If we can't find it, we're probably in a template.
        $navFinder="Java/global/percussion/widgetcontentfinder/perc_NavWidgetContentFinder";
        $navContainer = $rx.pageutils.widgetContents($sys.assemblyItem, $perc.widget, $navFinder, null, true);
        $navLoc = "";
        $navSelf = "";
        $blogIndexName = "";
        $blogPostTempId = null;
        if ($navContainer != null && ! $navContainer.isEmpty() ) {
            $navLoc = $navContainer.get(0).getFolderPath();
            $landingPage = $navContainer.get(0).getNode().getProperty("nav:landingPage");
            if($landingPage != null){
                $navSelf = $navContainer.get(0).getNode().getProperty("nav:landingPage").getNode();
                $blogIndexName = $navSelf.getName();
                $blogPostTempId = $rx.pageutils.getBlogPostTemplateId($navLoc);
            }
        }
        $siteName = "";
        $pagePath = "";
        $criteria = "[";
        if ($navLoc != null && $navLoc != "" && $navLoc != "//Sites")
        {
            $criteria = $criteria + "&quot;type = 'page'&quot;";
            $criteriaSep = ", ";
            $temppath = $navLoc.replace("//Sites/", "");
            $pagePath = $navLoc.replace("//Sites", "");
            if($temppath.indexOf("/") > 0)
            {
                $temppaths = $temppath.split("/");
                $criteria = $criteria  +  $criteriaSep +  "&quot;site = '" + $temppaths[0] + "'&quot;";
                $siteName = $temppaths[0];
                $folderpath = "";
                $counter = 1;
                while($counter < $temppaths.size())
                {
                    $folderpath = $folderpath + "/" + $temppaths[$counter];
                    $counter = $counter + 1;
                }
                $criteria = $criteria  +  $criteriaSep +  "&quot;folder LIKE '" + $folderpath + "/%'&quot;";
            }
            else
            {
                $criteria = $criteria  +  $criteriaSep +  "&quot;site = '" + $temppath + "'&quot;";
                $siteName = $temppath;
            }

            if ($blogPostTempId != null)
            {
               $templateNameList = $rx.pageutils.templateNames($blogPostTempId);
               if($templateNameList.size() > 0)
               {
                   $criteria = $criteria  +  $criteriaSep +  "&quot;dcterms:source = '" + $templateNameList.get(0).toString() + "'&quot;";
               }
            }

            ## Get tags and category values only if one of the conditions below is matched
            $tagValues = "";
            $categoryValues = "";
            if(($blogPostTempId == null) || (! $perc.widgetContents.isEmpty() && $pagePath != ""))
            {
               ## Get tag and categories values, only when we are not in a template.
               $tagValues = $sys.item.getProperty('page_tags').getValues();
               $rx.pageutils.alphaOrderTag($tagValues);

               $categoryValues = $sys.item.getProperty('page_categories_tree').getValues();
               $rx.pageutils.alphaOrderCategory($categoryValues);
           }

        }

		$pageId = $perc.page.id;
        if(!empty($pageId))
        {
           $finderPath = $rx.pageutils.getItemPath($pageId);
           $pathParts  = $finderPath.split("\/");
           $siteName   = $pathParts[2];
        }
        $criteria = $criteria + "]";

        ##Builds the criteria

        ##builds the order by clause
        $orderby = "dcterms:created desc";

        ##Build the query string for the delivery side

        $dynamicListData = "{" + "&quot;criteria&quot;:" + $criteria;
        $dynamicListData = $dynamicListData + ", &quot;navType&quot;:&quot;" + $blogNavigationContext + "&quot;";
        $dynamicListData = $dynamicListData + ", &quot;orderBy&quot;:&quot;" + $orderby + "&quot;";
        $dynamicListData = $dynamicListData + ", &quot;blogPostNextPost&quot;:&quot;" + $blogPostNextPost + "&quot;";
        $dynamicListData = $dynamicListData + ", &quot;blogPostPrePost&quot;:&quot;" + $blogPostPrePost + "&quot;";
        $dynamicListData = $dynamicListData + ", &quot;blogPostFullPath&quot;:&quot;/" + $siteName + $blogPostFullPath + "&quot;";
        $dynamicListData = $dynamicListData + ", &quot;folderPath&quot;:&quot;" + $pagePath + "&quot;";
        $dynamicListData = $dynamicListData + ", &quot;siteName&quot;:&quot;" +  $siteName + "&quot;";
        $dynamicListData = $dynamicListData + ", &quot;trackBlogPost&quot;:" +  $trackBlogPost;
        $dsUrl = $rx.pageutils.getDeliveryServer($sys.assemblyItem.PubServerId);
        $dynamicListData = $dynamicListData + ", &quot;deliveryurl&quot; : &quot;" + $dsUrl + "&quot;";
        $dynamicListData = $dynamicListData + ", &quot;isEditMode&quot; : &quot;" + $isEditMode + "&quot;";

        $dynamicListData = $dynamicListData + ", &quot;blogIndexName&quot; : &quot;" + $blogIndexName + "&quot;";
        $dynamicListData = $dynamicListData + "}";
    }
